function ajaxFormSubmit(){var e=$("#contact-form").serialize();return $.ajax({type:"POST",url:"php/mail.php",data:e,success:function(e){$("#contact-form").slideUp(800),$("#answer").html(e)}}),!1}$("#contact-form").validate({rules:{name:{required:!0},email:{required:!0,email:!0},message:{required:!0}},messages:{name:"Пожалуйста, введите свое имя",email:{required:"Пожалуйста, введите свой email",email:"Email адрес должен быть в формате name@domain.com . Возможно вы ввели email с ошибкой."},message:"Пожалуйста, введите текст сообщения"},submitHandler:function(e){ajaxFormSubmit()}});